class Cuadruplo:     # Funcion que inicializa a la clase cuadruplo    def __init__(self):        self.operador = ''        self.operando1 = 0        self.operando2 = 0        self.resultado = ""    # Funcion que permite establecer el valor del operador de un cuadruplo    def set_operador(self, operador):         self.operador = operador    # Funcion que permite establecer el valor del primer operando de un cuadruplo    def set_operando1(self, operando1):        self.operando1 = operando1    # Funcion que permite establecer el valor del segundo operando de un cuadruplo    def set_operando2(self, operando2):        self.operando2 = operando2    # Funcion que permite establecer el valor del resultado de un cuadruplo    def set_resultado(self, resultado):        self.resultado = resultado    # Funcion que permite obtener el valor del operador de un cuadruplo    def get_operador(self):         return self.operador    # Funcion que permite obtener el valor del primer operando de un cuadruplo    def get_operando1(self):        return self.operando1    # Funcion que permite obtener el valor del segundo operando de un cuadruplo    def get_operando2(self):        return self.operando2    # Funcion que permite obtener el valor del resultado de un cuadruplo    def get_resultado(self):        return self.resultado    # Funcion que permite imprimir los contenidos de un cuadruplo    def print_cuadruplo(self):        print(self.operador, self.operando1, self.operando2, self.resultado)            